mapper.select:
SELECT 
FROM 
	BOSS 
WHERE 
	BID = #{bid} 
	AND BPHONE = #{bphone}

mapper.delete:
DELETE FROM BOSS 
WHERE BID = #{bid}
AND BPW = #{bpw}

mapper.update:
UPDATE BOSS SET 
	BPW = #{bpw}
	, BPHONE = #{bphone} 
WHERE 
	BID = #{bid}

mapper.insert:
		
			SELECT COUNT(*) FROM ORDERS WHERE SNUM = #{snum}
      	
		INSERT INTO ORDERS(
			ORD
			,SNUM
			,ONAME
			,OPRICE
			,OIMAGE
		)VALUES(
			#{ord}
			,#{snum}
			,#{oname}
			,#{oprice}
			,#{oimage}
		)

UPDATE ORDERS 
SET
	ONAME = #{oname}
	,OPRICE = #{oprice}
	,OIMAGE = #{oimage}
WHERE  
	ORD = #{ord}
	AND SNUM = #{snum}

mapper.select:
SELECT * FROM GRADES WHERE SNUM IN 
	
		#{snum} 
	
AND GCURRENT = 'Y'

SELECT * FROM DETAILS WHERE GNUM IN
	
		#{gnum}
	
 AND DTYPE = 'Y' AND DCHECK = 'N' ORDER BY DDATE

SELECT * FROM DETAILS WHERE GNUM IN
	
		#{gnum}
	
	AND DDATE LIKE #{Ddate} AND DTYPE = 'Y' AND DCHECK = 'N'

SELECT * FROM GRADES WHERE MID IN
	
		#{mid}
	
	 AND GCURRENT = 'Y'

